import { combineReducers } from "redux";
import { createReducer } from "@reduxjs/toolkit";
import contactsActions from './contacts-actions';

const itemReducer = createReducer([{id: 'id-1', name: 'Rosie Simpson', number: '459-12-56'},
        {id: 'id-2', name: 'Hermione Kline', number: '443-89-12'},
        {id: 'id-3', name: 'Eden Clements', number: '645-17-79'},
        {id: 'id-4', name: 'Annie Copeland', number: '227-91-26'},], {
    [contactsActions.addContact]: (state, action) => [...state, action.payload],
    [contactsActions.deleteContact]: (state, action) => state.filter(contact => contact.id !== action.payload),
});

const filterReducer = createReducer('', {
    [contactsActions.changeFilter]: (_, action) => action.payload,
});

export default combineReducers({
    items: itemReducer,
    filter: filterReducer,
});
