import { combineReducers } from "redux";
import { createReducer } from "@reduxjs/toolkit";
import contactsActions from './contacts-actions';

const phonebook = createReducer('', {
    [contactsActions.addContact]: (state, action) => [...state, action.payload],
    [contactsActions.deleteContact]: (state, action) => state.filter(contact => contact.id !== action.payload),
});

const filter = createReducer('', {
    [contactsActions.changeFilter]: (_, action) => action.payload,
});

export default combineReducers({
    phonebook,
    filter
});
